SELECT id, business_id AS businessId, file_path AS filePath, file_ext AS fileExt, file_size AS fileSize, upload_user_id AS uploadUserId, upload_user_name AS uploadUserName, file_name AS fileName, type, created_at AS createdAt, updated_at AS updatedAt from uq_attachment
and upload_user_name LIKE concat ('%',#{uploadUserName},'%')
and file_name LIKE concat ('%',#{fileName},'%')
and created_at >= #{startDate}
and created_at <= #{endDate}
ORDER BY created_at DESC

