mapper.select:
select project_name
from project_contract_signing
where project_name = #{projectName}

select

from project_contract_signing
where serial_number = #{serialNumber}
ORDER BY create_time DESC;

select
from project_contract_signing where id IN
#{item}

select
from project_contract_signing where business_id IN
#{item}

select

from project_contract_signing


        and creator_org_id = #{creatorOrgId}


        and serial_number LIKE concat('%',#{serialNumber},'%')


        and project_name LIKE concat('%',#{projectName},'%')


        and project_leader_name LIKE concat('%',#{projectLeaderName},'%')


        and leader_post_name LIKE concat('%',#{leaderPostName},'%')


        and creator_user_name LIKE concat('%',#{creatorUserName},'%')


        and unit_name LIKE concat('%',#{unitName},'%')


        and create_time  >=  #{beginCreateTime}


        and create_time  <=  #{lastCreateTime}



        and update_time  >=  #{beginUpdateTime}


        and update_time  <=  #{lastUpdateTime}



        and CONVERT ( VARCHAR ( 30 ), start_date, 23 )  >=  #{beginStartDate}


        and  CONVERT ( VARCHAR ( 30 ), start_date, 23 )  <=  #{lastStartDate}



        and CONVERT ( VARCHAR ( 30 ), end_date, 120 )  >=  #{beginEndDate}


        and  CONVERT ( VARCHAR ( 30 ), end_date, 120 )  <=  #{lastEndDate}


        and contract_status_code in

            #{item}



        and secrets_code in

            #{item}



ORDER BY update_time DESC;
