mapper.select:
select

from sys_organization_info


        and org_name LIKE concat('%',#{orgName},'%')


        and org_number LIKE concat('%',#{orgNumber},'%')


        and status = #{status}


        and remark LIKE concat('%',#{remark},'%')


        and create_user LIKE concat('%',#{createUserName},'%')


        and update_user LIKE concat('%',#{updateUser},'%')


        and create_time  >=  #{beginCreateTime}


        and create_time  <=  #{lastCreateTime}



        and update_time  >=  #{beginUpdateTime}


        and update_time  <=  #{lastUpdateTime}


ORDER BY update_time DESC;

select
from sys_organization_info where id IN
#{item}
