mapper.select:
select
from item_closure_check
(process_status != #{processStatus} or creator_user_id=#{createUserId}) and creator_org_id = #{creatorOrgId}
and serial_number LIKE concat('%',#{serialNumber},'%')
and job_title LIKE concat('%',#{jobTitle},'%')
and approve_user_name LIKE concat('%',#{approveUserName},'%')
and creator_user LIKE concat('%',#{creatorUser},'%')
and created_date LIKE concat('%',#{createdDate},'%')
and create_user LIKE concat('%',#{createUser},'%')
and approve_user_name LIKE concat('%',#{applyUserName},'%')
and post_name LIKE concat('%',#{postName},'%')
and start_year >= #{startYear}
and end_year <= #{endYear}
and process_status in
#{item}
ORDER BY update_time DESC;

select id, business_id as businessId serial_number as serialNumber, project_name as projectName, apply_user_name as applyUserName, apply_user_id as applyUserId, post_code as postCode, post_name as postName, telephone, start_year as startYear, end_year as endYear from project_apply_main
process_status != #{processStatus}
and serial_number LIKE concat('%',#{serialNumber},'%')
and project_name LIKE concat('%',#{projectName},'%')
and apply_user_name LIKE concat('%',#{applyUserName},'%')
and post_name LIKE concat('%',#{postName},'%')
and telephone LIKE concat('%',#{telephone},'%')
and start_year >= #{startYear}
and end_year <= #{endYear}
ORDER BY update_time DESC;
