mapper.select:
select project_name
from project_contract_signing
where project_name = #{projectName}

select

from project_contract_signing
where serial_number = #{serialNumber}
ORDER BY create_time DESC;

select
from project_contract_signing where id IN
#{item}

select
from project_contract_signing where business_id IN
#{item}

select

from project_contract_signing


        and serial_number LIKE concat('%',#{serialNumber},'%')


        and project_name LIKE concat('%',#{projectName},'%')


        and project_leader_name LIKE concat('%',#{projectLeaderName},'%')


        and leader_post_name LIKE concat('%',#{leaderPostName},'%')


        and creator_user_name LIKE concat('%',#{creatorUserName},'%')


        and unit_name LIKE concat('%',#{unitName},'%')


        and start_date  >=  #{startDate}


        and end_date  <=  #{endDate}


        and contract_status_code = #{contractStatusCode}


        and secrets_code = #{secretsCode}


ORDER BY start_date DESC;
